import Foundation

extension Environment {
    var jwtPublicKey: Data {
        switch self {
            case .dev:
                return Data(base64Encoded: "")!
            case .abnahme:
                return Data(base64Encoded: "")!
            case .prod:
                return Data(base64Encoded: "")!
        }
    }

    var configJwtPublicKey: Data {
        switch self {
            case .dev:
                return Data(base64Encoded: "")!
            case .abnahme:
                return Data(base64Encoded: "")!
            case .prod:
                return Data(base64Encoded: "")!
        }
    }
}
